import React, { useState, useEffect } from "react";
import { useParams, useHistory } from "react-router-dom";
import RatingStars from "./Rating";
// import Search from "./Search";

function CourseDetails() {
  const [courseDetails, setCourseDetails] = useState(null);
  const { id } = useParams();
  const history = useHistory();

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch(
          `https://tap-web-1.herokuapp.com/topics/details?id=${id}`
        );
        if (!response.ok) {
          throw new Error(
            "Something went wrong. Course details failed to load."
          );
        }
        const result = await response.json();
        setCourseDetails(result);
      } catch (error) {
        console.log(error);
        // Handle error gracefully (e.g., display an error message to the user)
      }
    };

    fetchData();

    // Cleanup function
    return () => {
      // Perform any cleanup if needed
    };
  }, [id]); // Dependency on 'id' ensures useEffect runs whenever 'id' changes

  const handleClick = () => {
    history.push(`/details?id=${id}`);
  };

  return (
    <div className="course-details-container">
      {courseDetails && (
        <>
          <img
            src={`./Logos/${courseDetails.image}`}
            alt={courseDetails.topic}
          />
          <div className="course-details-content">
            <p className="category-container">{courseDetails.category}</p>
            <h4>{courseDetails.topic}</h4>
            <RatingStars numStars={5} />
            <p className="auther-name">Author: {courseDetails.name}</p>
            <button onClick={handleClick}>Go Back</button>
          </div>
        </>
      )}
    </div>
  );
}

export default CourseDetails;
