import React, { useState, useEffect } from "react";
import RatingStars from "./Rating";
import SearchComponent from "./Search";
import SortComponent from "./Sort";
import FilterComponent from "./Filtter";

function CoursesContainer() {
  const [contentData, setContentData] = useState([]);
  const [searchInputValue, setSearchInputValue] = useState("");
  const [sortSelectValue, setSortSelectValue] = useState("");
  const [filterSelectValue, setFilterSelectValue] = useState("");

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      const response = await fetch(
        "https://tap-web-1.herokuapp.com/topics/list"
      );
      if (!response.ok) {
        throw new Error("Something went wrong. Web topics failed to load.");
      }
      const result = await response.json();
      setContentData(result);
    } catch (error) {
      console.log(error);
    }
  };

  const handleClick = (id) => {
    console.log("Clicked on topic with ID:", id);
    // Handle click event, e.g., redirect to details page
    // window.location = `details.html?id=${id}`;
  };

  return (
    <div className="courses-container container">
      <SearchComponent
        searchInputValue={searchInputValue}
        setSearchInputValue={setSearchInputValue}
        fetchData={fetchData}
      />
      <SortComponent
        sortSelectValue={sortSelectValue}
        setSortSelectValue={setSortSelectValue}
      />
      <FilterComponent
        filterSelectValue={filterSelectValue}
        setFilterSelectValue={setFilterSelectValue}
      />
      {contentData.map((item) => (
        <div key={item.id} className="courseBox">
          <img
            src={`./Logos/${item.image}`}
            className="courses-img"
            alt={item.topic}
          />
          <div className="course-Box-Content-container">
            <p className="category-container">{item.category}</p>
            <h4
              className={`topic-${item.id}`}
              onClick={() => handleClick(item.id)}
            >
              {item.topic}
            </h4>
            <RatingStars numStars={5} />
            <p className="auther-name">Author: {item.name}</p>
          </div>
        </div>
      ))}
    </div>
  );
}

export default CoursesContainer;

