import React, { useEffect, useRef } from "react";

function SearchComponent({ searchInputValue, setSearchInputValue, fetchData }) {
  const debouncedHandleSearch = useRef(null);
  const debounceDelay = 300;

  useEffect(() => {
    debouncedHandleSearch.current = debounce(() => {
      fetchData();
    });
  }, [searchInputValue]);

  const debounce = (func, delay) => {
    let timeoutId;
    return function () {
      const context = this;
      const args = arguments;

      clearTimeout(timeoutId);
      timeoutId = setTimeout(() => {
        func.apply(context, args);
      }, delay);
    };
  };

  const searchInputChange = (event) => {
    setSearchInputValue(event.target.value);
    debouncedHandleSearch.current();
  };

  return (
    <div className="search-bar container">
      {/* Search Input */}
      <div className="search-input">
        <span className="mode-icon-search" />
        <input
          className="search-box"
          type="text"
          id="search-input"
          placeholder="Search the website.."
          name="search"
          value={searchInputValue}
          onChange={searchInputChange}
        />
      </div>
    </div>
  );
}

export default SearchComponent;

