import React, { useState, useEffect, useRef } from "react";
import RatingStars from "./Rating";
import { Link, NavLink } from "react-router-dom";
import { useNavigate } from "react-router-dom";
// import Search from "./Search";

function CoursesContainer() {
  const [contentData, setContentData] = useState([]);
  const [searchInputValue, setSearchInputValue] = useState("");
  const debouncedHandleSearch = useRef(null);
  const [sortSelectValue, setSortSelectValue] = useState("");
  const [filterSelectValue, setFilterSelectValue] = useState("");
  const navigate = useNavigate();

  const handleClickDEtails = (id) => {
    navigate(`/details?id=${id}`);
  };

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch(
          "https://tap-web-1.herokuapp.com/topics/list"
        );
        if (!response.ok) {
          throw new Error("Something went wrong. Web topics failed to load.");
        }
        const result = await response.json();
        setContentData(result);
      } catch (error) {
        console.log(error);
      }
    };

    fetchData();

    // Cleanup function
    return () => {
      // Perform any cleanup if needed
    };
  }, []); // Empty dependency array ensures the effect runs only once on component mount

  //   const handleClick = (id) => {
  //     console.log("Clicked on topic with ID:", id);
  //     // Handle click event, e.g., redirect to details page
  //     // window.location = `details.html?id=${id}`;
  //   };

  useEffect(() => {
    debouncedHandleSearch.current = debounce(() => {
      fetch(
        `https://tap-web-1.herokuapp.com/topics/list?phrase=${searchInputValue}`
      )
        .then((response) => response.json())
        .then((result) => {
          setContentData(result);
          console.log("result", result);
        });
    });
  }, [searchInputValue]);

  const debounce = (func, delay) => {
    let timeoutId;
    return function () {
      const context = this;
      const args = arguments;

      clearTimeout(timeoutId);
      timeoutId = setTimeout(() => {
        func.apply(context, args);
      }, delay);
    };
  };
  function searchInputChange(event) {
    setSearchInputValue(event.target.value);
    debouncedHandleSearch.current();
    console.log("setSearchInputValue");
  }

  //sort function
  const sortBy = (value) => {
    let sortedData = [...contentData];

    switch (value) {
      case "Topic":
        sortedData.sort((a, b) => a.topic.localeCompare(b.topic));
        break;
      case "Author":
        sortedData.sort((a, b) => a.name.localeCompare(b.name));
        break;
      default:
        break;
    }
    // console.log(sortSelectValue);

    setContentData(sortedData);
  };

  const handleSortChange = (event) => {
    const value = event.target.value;
    // setSortSelectValue(value);
    console.log("event", event.target.value);
    console.log("setSortSelectValue", sortSelectValue);
    sortBy(value);
  };

  //filtter
  const filterBy = (filtterValue) => {
    let filteredData = [...contentData];

    if (filtterValue) {
      // If a category is selected, filter the data based on the selected category
      filteredData = filteredData.filter(
        (course) => course.category === filtterValue
      );
    }

    setContentData(filteredData);
  };

  const handleFilterChange = (event) => {
    const filtterValue = event.target.value;
    // console.log("setFilterSelectValue", filterSelectValue);
    filterBy(filtterValue);
  };

  const addContent = (result) => {
    return result.map((item) => (
      <div
        key={item.id}
        className="courseBox"
        onClick={() => handleClickDEtails(item.id)}
      >
        <img
          src={`./Logos/${item.image}`}
          className="courses-img"
          alt={item.topic}
        />
        <div className="course-Box-Content-container">
          <p className="category-container">{item.category}</p>
          <h4
            className={`topic-${item.id}`}
            //   onClick={() => handleClick(item.id)}
          >
            {item.topic}
          </h4>
          <RatingStars numStars={5} />
          <p className="auther-name">Author: {item.name}</p>
        </div>
      </div>
    ));
  };

  return (
    <>
      <div className="search-bar container">
        {/* typo */}
        <div className="searcch-input">
          <span className="mode-icon-search" />
          <input
            className="search-box"
            type="text"
            id="search-input"
            placeholder="Search the website.."
            name="search"
            value={searchInputValue}
            onChange={searchInputChange}
          />
        </div>
        <div className="search-box-buttons">
          {/* {sortBy(sortSelectValue)} */}
          <div className="sort-select">
            <label htmlFor="sort">Sort by:</label>
            <select
              name="sort"
              id="sort"
              //   value={sortSelectValue}
              onChange={handleSortChange}
            >
              <option value="defult">Default</option>
              <option value="Topic">Topic Title</option>
              <option value="Author">Author Name</option>
            </select>
          </div>
          <div className="filter-select">
            <label htmlFor="filter">Filter by:</label>
            <select
              name="filter"
              id="filter"
              //   value={filterSelectValue}
              onChange={handleFilterChange}
            >
              <option value="defult">Default</option>
              <option value="Web Development Languages">
                Web Development Languages
              </option>
              <option value="Frontend Frameworks and Libraries">
                Frontend Frameworks and Libraries
              </option>
              <option value="Backend Frameworks and Libraries">
                Backend Frameworks and Libraries
              </option>
              <option value="Databases and APIs">Databases and APIs</option>
              <option value="Web Development Concepts and Technologies">
                Web Development Concepts and Technologies
              </option>
            </select>
          </div>
        </div>
      </div>

      <div className="courses-container container">
        {addContent(contentData)}
      </div>
    </>
  );
}

export default CoursesContainer;
