import React, { useState, useEffect } from "react";
// import React, { useState, useEffect } from "react";
import RatingStars from "./Rating";

function CoursesContainer() {
  const [contentData, setContentData] = useState([]);
  const [searchInputValue, setSearchInputValue] = useState("");
  const [sortSelectValue, setSortSelectValue] = useState("");
  const [filterSelectValue, setFilterSelectValue] = useState("");

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      const response = await fetch(
        "https://tap-web-1.herokuapp.com/topics/list"
      );
      if (!response.ok) {
        throw new Error("Something went wrong. Web topics failed to load.");
      }
      const result = await response.json();
      setContentData(result);
    } catch (error) {
      console.log(error);
    }
  };

  const debounce = (func, delay) => {
    let timeoutId;
    return function () {
      const context = this;
      const args = arguments;

      clearTimeout(timeoutId);
      timeoutId = setTimeout(() => {
        func.apply(context, args);
      }, delay);
    };
  };

  const debouncedHandleSearch = debounce(async () => {
    try {
      const response = await fetch(
        `https://tap-web-1.herokuapp.com/topics/list?phrase=${searchInputValue}`
      );
      if (!response.ok) {
        throw new Error("Something went wrong. Search failed.");
      }
      const result = await response.json();
      addContent(result);
    } catch (error) {
      console.log(error);
    }
  }, 300);

  const handleSearchInputChange = (event) => {
    setSearchInputValue(event.target.value);
    debouncedHandleSearch();
  };

  const sortBy = () => {
    let sortedData = [...contentData];

    switch (sortSelectValue) {
      case "Topic":
        sortedData.sort((a, b) => a.topic.localeCompare(b.topic));
        break;
      case "Author":
        sortedData.sort((a, b) => a.name.localeCompare(b.name));
        break;
      default:
        break;
    }

    addContent(sortedData);
  };

  const filterBy = () => {
    let filteredData = [...contentData];

    if (filterSelectValue) {
      filteredData = filteredData.filter(
        (course) => course.category === filterSelectValue
      );
    }

    const courseBox = document.querySelectorAll(".courseBox");
    for (let i = 0; i < courseBox.length; i++) {
      courseBox[i].remove();
    }

    addContent(filteredData);
  };

  const addContent = (result) => {
    setContentData(result);
  };

  return (
    <div>
      <input
        type="text"
        id="search-input"
        placeholder="Search"
        value={searchInputValue}
        onChange={handleSearchInputChange}
      />
      <select
        id="sort"
        value={sortSelectValue}
        onChange={(e) => setSortSelectValue(e.target.value)}
      >
        <option value="Topic">Sort by Topic</option>
        <option value="Author">Sort by Author</option>
      </select>
      <select
        id="filter"
        value={filterSelectValue}
        onChange={(e) => setFilterSelectValue(e.target.value)}
      >
        <option value="">Filter by</option>
        <option value="Web Development">Web Development</option>
        <option value="Programming">Programming</option>
      </select>
      <div className="courses-container">
        {contentData.map((item) => (
          <div key={item.id} className="courseBox">
            <img
              src={`./logos/${item.image}`}
              className="courses-img"
              alt={item.topic}
            />
            <div className="course-Box-Content-container">
              <p className="category-container">{item.category}</p>
              <h4
                className={`topic-${item.id}`}
                onClick={() => handleClick(item.id)}
              >
                {item.topic}
              </h4>
              <div className="rating">{createRatingStars(5)}</div>
              <p className="auther-name">Author: {item.name}</p>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}

export default CoursesContainer;

